import React from 'react'
import Particles from "react-tsparticles"
import { Footer } from './components/Footer'
import { GlobalStyle } from './assets/styles/global'
import 'react-toastify/dist/ReactToastify.css'
import { BrowserRouter as Router ,Routes, Route} from 'react-router-dom'
import Team from './pages/Team';
import Intro from './pages/Intro';
import Projects from './pages/Projects';
import {Container} from "./assets/styles/particle";
import { Header } from "./components/Header";
import Testimonial from './pages/testimonial'


function App() {
  return (
    <>
      <GlobalStyle></GlobalStyle>
      <Router>
      <Container>
      <Particles
      id="tsparticles"
      options={{
        "particles": {
          "number": {
            "value": 160,
            "density": {
              "enable": true,
              "value_area": 800
            }
          },
          "color": {
            "value": "#ffffff"
          },
          "shape": {
            "type": "circle",
            "stroke": {
              "width": 0,
              "color": "#000000"
            },
            "polygon": {
              "nb_sides": 5
            },
            "image": {
              "src": "img/github.svg",
              "width": 100,
              "height": 100
            }
          },
          "opacity": {
            "value": 1,
            "random": true,
            "anim": {
              "enable": true,
              "speed": 1,
              "opacity_min": 0,
              "sync": false
            }
          },
          "size": {
            "value": 3,
            "random": true,
            "anim": {
              "enable": false,
              "speed": 4,
              "size_min": 0.3,
              "sync": false
            }
          },
          "line_linked": {
            "enable": false,
            "distance": 150,
            "color": "#ffffff",
            "opacity": 0.4,
            "width": 1
          },
          "move": {
            "enable": true,
            "speed": 1,
            "direction": "none",
            "random": true,
            "straight": false,
            "out_mode": "out",
            "bounce": false,
            "attract": {
              "enable": false,
              "rotateX": 600,
              "rotateY": 600
            }
          }
        },
        "interactivity": {
          "detect_on": "canvas",
          "events": {
            "onhover": {
              "enable": true,
              "mode": "bubble"
            },
            "onclick": {
              "enable": true,
              "mode": "repulse"
            },
            "resize": true
          },
          "modes": {
            "grab": {
              "distance": 400,
              "line_linked": {
                "opacity": 1
              }
            },
            "bubble": {
              "distance": 250,
              "size": 0,
              "duration": 2,
              "opacity": 0,
              // "speed": 3 ,
            },
            "repulse": {
              "distance": 400,
              "duration": 0.4
            },
            "push": {
              "particles_nb": 4
            },
            "remove": {
              "particles_nb": 2
            }
          }
        },
        "retina_detect": true
      }}/>
      <Header></Header>
      <Routes>
          <Route path="/" element={<Intro/>}/>
          <Route path="/web" element={<Intro/>}/>
          <Route path="/team" element={<Team/>}/>
          <Route path="/projects" element={<Projects/>}/>
          <Route path="/testimonial" element={<Testimonial/>}/>
      </Routes>
      </Container>
      </Router>
      <Footer/>
    </>
  )
}

export default App
